
/**
 * Method is used to run simple unit test
 * @param {*} mod - Name of the function | string 
 * @param {*} msg - Message | string
 * @param {*} method - the function you are running the unit test for.
 * @param {*} answer - The expected out of the function
 */
export function assertEqual(mod, msg, method, answer){
    QUnit.module(mod, () => {
        QUnit.test(msg, function(assert){
            assert.equal(method(), answer);
        })
    });
}

/**
 * Method is used to run deep unit test
 * @param {*} mod - Name of the function | string 
 * @param {*} msg - Message | string
 * @param {*} method - the function you are running the unit test for.
 * @param {*} answer - The expected out of the function
 */
export function assertDeepEqual(mod, msg, method, answer){
    QUnit.module(mod, () => {
        QUnit.test(msg, function(assert){
            assert.deepEqual(method(), answer);
        })
    });
}